import React, { useState } from 'react';
import axios from 'axios';  // Import Axios for making HTTP requests
import './SignUp.css';  // Import the SignUp-specific styles

function SignUp() {
  // State to manage form inputs
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  // State for error/success messages
  const [message, setMessage] = useState('');

  // Handle form field change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Validate form fields
  const validateForm = () => {
    const { username, email, password } = formData;

    // Check if fields are empty
    if (!username || !email || !password) {
      setMessage('Please fill in all fields.');
      return false;
    }

    // Check email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setMessage('Please enter a valid email address.');
      return false;
    }

    // Check password strength (min 6 characters)
    if (password.length < 6) {
      setMessage('Password must be at least 6 characters.');
      return false;
    }

    return true;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form data
    if (validateForm()) {
      try {
        // Send POST request to backend API
        const response = await axios.post('http://localhost:5000/api/signup', formData);

        // Show success message
        setMessage('User signed up successfully!');

        // Clear form
        setFormData({
          username: '',
          email: '',
          password: ''
        });

        console.log(response.data);  // You can log the response from the backend (for debugging)
      } catch (error) {
        console.error('Error during sign-up:', error);
        setMessage('Error signing up. Please try again.');  // Show error message
      }
    }
  };

  return (
    <div className="sign-up-main-content">
      <div className="sign-up-container">
        <h2>Sign Up Jopi</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <button type="submit">Sign Up</button>
          </div>
        </form>

        {message && <p>{message}</p>} {/* Display message (success or error) */}

        {/* Optional: Link to Login Page */}
        <p>Already have an account? <a href="/login">Log In</a></p>
      </div>
    </div>
  );
}

export default SignUp;
