import React, { useState } from 'react';
import './Header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Manage login state
  const [showMenu, setShowMenu] = useState(false); // Manage dropdown menu visibility

  const handleLogout = () => {
    // Perform logout logic (e.g., remove token)
    setIsLoggedIn(false);
    localStorage.removeItem('token'); // Example of token removal
  };

  const toggleMenu = () => {
    setShowMenu((prev) => !prev);
  };

  return (
    <header className="header">
      <div className="logo">
        1:1
      </div>
      <nav>
        <ul className="nav-links">
          <li className="nav-item">
            <a href="/" className="nav-link">Home</a>
          </li>
          <li className="nav-item">
            <a href="/about" className="nav-link">About</a>
          </li>
        </ul>
        {/* Profile Section */}
        <div className="profile-section">
          <div className="profile-circle" onClick={toggleMenu}>
            <img
              src="https://via.placeholder.com/40" // Replace with user's profile picture
              alt="Profile"
              className="profile-image"
            />
          </div>
          {showMenu && (
            <div className="dropdown-menu">
              {isLoggedIn ? (
                <button onClick={handleLogout} className="dropdown-item">Log Out</button>
              ) : (
                <a href="/login" className="dropdown-item">Log In</a>
              )}
            </div>
          )}
        </div>
      </nav>
    </header>
  );
}

export default Header;
